import React, { Component } from 'react';
class Login extends Component {

	submitLogin = () => {
		var userList = [
			{
				'id' : 1,
				'username' : 'admin',
				'password' : '123456',
				'role_id' : 1,
				'status' : 1
			},
			{
				'id' : 2,
				'username' : 'hoangmn',
				'password' : '123456',
				'role_id' : 2,
				'status' : 1
			},
			{
				'id' : 3,
				'username' : 'user01',
				'password' : '123456',
				'role_id' : 2,
				'status' : 3
			},
			{
				'id' : 4,
				'username' : 'user02',
				'password' : '123456',
				'role_id' : 3,
				'status' : 1
			}
		];
		var username = this.refs.username;
		var password = this.refs.password;
		var loginStatus = userList.map((userObj,index) => {
            if(index.username== username){
            	if(index.password == password){
            		return 200; //login Success
            	}else{
            		return 201; //wrong pasword
            	}
            }else{
            	return 203 ; // Do not exist
            }
        })
		console.log(loginStatus);
	}

	render() {
		return (
			<section id="login">
				<div className="contact">
					<div className="container">
						<div className="row">
							<div className="col-lg-3"></div>
							<div className="col-lg-6">
								<div className="contact_form_container">
									<div className="contact_title">Login</div>
									<form action="#" className="contact_form" id="contact_form">
									<input type="text" className="contact_input" placeholder="Username" required="required" ref="username" />
									<input type="password" className="contact_input" placeholder="Password" required="required" ref="password" />
									<button className="contact_button" onClick={ this.submitLogin } >Login</button>
									</form>
								</div>
							</div>
							<div className="col-lg-3"></div>
						</div>
					</div>
				</div>
			</section>
		)
	}
}

export default Login;
